  user_email, user_password, dept_id, 
  user_status, remark, operator, 
  create_time, operate_time, operator_ip
  )
values (#{id,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{userTelephone,jdbcType=VARCHAR}, 
  #{userEmail,jdbcType=VARCHAR}, #{userPassword,jdbcType=VARCHAR}, #{deptId,jdbcType=INTEGER}, 
  #{userStatus,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, #{operator,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{operateTime,jdbcType=TIMESTAMP}, #{operatorIp,jdbcType=VARCHAR}
  )

mapper.select:
SELECT

  FROM mmall_sys_user
WHERE user_telephone = #{keyword}
OR user_email = #{keyword}

SELECT COUNT(1)
 FROM mmall_sys_user
WHERE user_email = #{mail}

  AND id != #{id}

SELECT COUNT(1)
  FROM mmall_sys_user
WHERE user_telephone = #{telephone}

  AND id != #{id}

SELECT count(1)
  FROM mmall_sys_user
WHERE dept_id = #{deptId}

SELECT

  FROM mmall_sys_user
WHERE dept_id = #{deptId}
ORDER BY  user_name ASC
LIMIT #{pageQuery.offset}, #{pageQuery.pageSize}
